    const [message, setMessage] = React.useState('hoh');
    function getInfo() {
        console.log('Hmmmmm');
        var proxyUrl = 'https://cors-anywhere.herokuapp.com/',
            targetUrl = 'https://jsonplaceholder.typicode.com/todos/1'
        fetch( targetUrl)
            .then(r => {
                return r.json();
            }).then(data => {
                setMessage(data.id);
            /*this.state.name = data.name;
