import React, {Component} from 'react';
import './Meal.css';
import SERVER_URL from './consts.js'
const Meal = (props) => {
    const some = props.match.params.name
    const [message, setMessage] = React.useState('?desc?');

    function getInfo() {
        console.log('getInfo called');
        fetch(SERVER_URL + '/' + some)
            .then(r => {
                return r.json();
            }).then(data => {
                setMessage(data.description);
        })
    }

    const [productName, setName] = React.useState('?name?');
    function getName() {
        console.log('getInfo called');
        fetch(SERVER_URL + '/' + some)
            .then(r => {
                return r.json();
            }).then(data => {
            setName(data.name);
        })
    }

    const [ingredients, setIngredients] = React.useState('?ingr?');
    function getIngredients() {
        console.log('getInfo called');
        fetch(SERVER_URL + '/' + some)
            .then(r => {
                return r.json();
            }).then(data => {
            //set ingredients when it will be ready
            setMessage(data.description);

        })
    }

    getInfo();
    getName();
    getIngredients();
    return(
        <div>
            <div className='centered'>
                <h1>This is {productName}</h1>
                <img src={require('/home/karen-1999/books/FullStackProject/examples/Hamova-project/frontend/src/images/' + some + '.jpg')}/>
            </div>
            <div className="row">
                <div className="column">
                    <div>
                        <h3>
                            Description
                        </h3>
                        {message}
                    </div>
                </div>
                <div className="column">
                    <div>
                        <h3>
                            Ingredients
                        </h3>
                        {ingredients}
                    </div>
                </div>
            </div>
        </div>
    );
}

